class TwilioWorker  # Esto lo sacan de la página de twilio  TWILIO_ACCOUNT_SID = "<REEMPLAZAR_POR_ACCOUNT_ID>"  # Esto lo sacan de la página de twilio  TWILIO_AUTH_TOKEN = "<REEMPLAZAR POR AUTH TOKEN>"  # Este es el número de teléfono de origen. Cuando crean la cuenta, se les asigna  # un número (pero tienen que confirmarlo)  TWILIO_FROM_NUMBER = "+19100000000"  # Este es el número de destinatario. Eventualmente pueden hacer cosas más interesantes,  # como permitir al usuario escoger el destinatario de SMS para cada tarea. Por el momento,  # solo es un número estático  # Nota: es importante poner el prefijo telefónico: +56900000000  TWILIO_DESTINATARY = "+56900000000"  def self.send_sms(message)    begin      @client = Twilio::REST::Client.new(TWILIO_ACCOUNT_SID, TWILIO_AUTH_TOKEN)      response = @client.account.messages.create({      	:from => TWILIO_FROM_NUMBER,      	:to => "+56972999115",      	:body => "ToDo app: #{message}",      })    rescue Exception  => exception      current_user.notify(exception)    end  end  def self.method(message)    Rails.logger.info message  endend